import React from "react";
import { useState } from "react";
import { NavLink } from "react-router-dom";
import Buttons from "../buttons/Buttons";
import mc from "./nav.module.scss";
import { getItem } from "../../utils/storage.utils";
import { useNavigate } from "react-router-dom";

const Nav = () => {
  const [isActive, setIsActive] = useState(false);
  const [navHide, setNavHide] = useState(false);
  const navigate = useNavigate();

  const handleClick = () => {
    setIsActive(!isActive);
    setNavHide(!navHide);
  };

  const isLogged = !!getItem("token");

  const logOut = () => {
    localStorage.removeItem("token");

    navigate("/");
  };

  return (
    <div>
      {isLogged ? (
        <div>
          <button
            onClick={handleClick}
            className={`${mc.burger} ${
              isActive ? mc.burgerClicked : !isActive
            }`}
          >
            <span></span>
          </button>
          <nav
            className={`${!navHide ? mc.navHide : navHide} ${mc.navConnected}`}
          >
            <ul>
              <li>
                <ul>
                  <li>
                    <NavLink to="/">Accueil</NavLink>
                  </li>
                  <li>
                    <NavLink to="/">Profil</NavLink>
                  </li>
                  <li>
                    <NavLink to="/">Amis</NavLink>
                  </li>
                  <li>
                    <NavLink to="/">Jeux</NavLink>
                  </li>
                  <li>
                    <button onClick={logOut}>Deconnexion</button>
                  </li>
                </ul>
              </li>
            </ul>
          </nav>
          <div
            onClick={handleClick}
            className={`${mc.overlay} ${isActive ? mc.show : !isActive}`}
          ></div>
        </div>
      ) : (
        <Buttons />
      )}
    </div>
  );
};

export default Nav;
