import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getOneGame } from "../../redux/reducers/game.slice";
import { getItem } from "../../utils/storage.utils";

const Game = () => {
  const { id } = useParams(); // récupérer l'ID du jeu depuis les paramètres d'URL
  const { games } = useSelector((store) => store.games);
  const dispatch = useDispatch();
  const token = getItem("token");

  const game = games.find((g) => {
    g.id === id;
    useEffect(() => {
      dispatch(getOneGame(id, token));
    }, []);
  }); // trouver le jeu correspondant dans la liste des jeux

  return (
    <div>
      {/* <h2>{game.name}</h2>
      <p>{game.scoreCount} games saved</p> */}
    </div>
  );
};

export default Game;
