import React from "react";
import { useState } from "react";
import { NavLink } from "react-router-dom";
import Buttons from "../buttons/Buttons";
import mc from "./nav.module.scss";
import { getItem } from "../../utils/storage.utils";
import { useDispatch, useSelector } from "react-redux";
import { userConnected } from "../../redux/reducers/user.slice";

const Nav = () => {
  const [isActive, setIsActive] = useState(false);
  const [navHide, setNavHide] = useState(false);

  const dispatch = useDispatch();

  const { isConnected } = useSelector((store) => store.user);

  const userIsConnected = () => {
    const token = getItem("token");
    // console.log(token);
    // if (token) {
    //   setIsConnected(true);
    // } else {
    //   setIsConnected(false);
    // }
    dispatch(userConnected(!isConnected));
  };

  const handleClick = () => {
    setIsActive(!isActive);
    setNavHide(!navHide);
  };

  return (
    <div>
      {userIsConnected ? (
        <div>
          <button
            onClick={handleClick}
            className={`${mc.burger} ${
              isActive ? mc.burgerClicked : !isActive
            }`}
          >
            <span></span>
          </button>
          <nav
            className={`${!navHide ? mc.navHide : navHide} ${mc.navConnected}`}
          >
            <ul>
              <li>
                <ul>
                  <li>
                    <NavLink to="/">Accueil</NavLink>
                  </li>
                  <li>
                    <NavLink to="/">Profil</NavLink>
                  </li>
                  <li>
                    <NavLink to="/">Amis</NavLink>
                  </li>
                  <li>
                    <NavLink to="/">Jeux</NavLink>
                  </li>
                </ul>
              </li>
            </ul>
          </nav>
          <div
            onClick={handleClick}
            className={`${mc.overlay} ${isActive ? mc.show : !isActive}`}
          ></div>
        </div>
      ) : (
        <Buttons />
      )}
    </div>
  );
};

export default Nav;
