import React from "react";
import { useState } from "react";
import { NavLink } from "react-router-dom";
import Buttons from "../buttons/Buttons";
import mc from "./nav.module.scss";

const Nav = () => {
  const [isActive, setIsActive] = useState(false);
  const [menu, setMenu] = useState(mc.menuHide);
  const [isVisible, setIsVisible] = useState(mc.overlay);
  const userConnected = () => {
    return true;
  };

  const handleClick = () => {
    setIsActive(!isActive);
    if (menu === mc.menuHide) {
      return setMenu(mc.showMenu);
    }
    if (menu === mc.showMenu) {
      return setMenu(mc.menuHide);
    }
    if (isVisible === mc.overlay) {
      return setIsVisible(mc.show);
    }
    if (isVisible === mc.show) {
      return setIsVisible(mc.overlay);
    }
  };
  return (
    <div>
      <nav>
        <ul>
          {!userConnected ? (
            <li>
              <button
                onClick={handleClick}
                className={` ${mc.burger} ${isActive ? mc.active : !isActive} `}
              >
                <span></span>
              </button>
              <ul className={menu}>
                <li>
                  <NavLink to="/">Home</NavLink>
                </li>
                <li>
                  <NavLink to="/">Profil</NavLink>
                </li>
                <li>
                  <NavLink to="/">Friends</NavLink>
                </li>
                <li>
                  <NavLink to="/">Games</NavLink>
                </li>
              </ul>
            </li>
          ) : (
            <Buttons />
          )}
        </ul>
      </nav>
      <div className={isVisible}></div>
    </div>
  );
};

export default Nav;
