import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getOneGame } from "../../redux/reducers/game.slice";
import { getGamePlayed } from "../../redux/reducers/gamePlayed.slice";
import { getItem } from "../../utils/storage.utils";
import mc from "./game.module.scss";

const Game = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const token = getItem("token");
  const { loading, game } = useSelector((store) => store.games);

  const { gamesPlayed } = useSelector((store) => store.gamesPlayed);
  const gamePlayed = gamesPlayed.filter((gp) => gp.game_id === game.id);
  console.log(gamePlayed);

  useEffect(() => {
    dispatch(getGamePlayed(token));
  }, []);

  useEffect(() => {
    dispatch(getOneGame(id, token));
  }, [dispatch, id]);
  console.log(game);

  return (
    <div>
      {loading && <p>Loading...</p>}
      {game && (
        <div className={mc.gameContainer}>
          <img src="" alt="une photo du jeu" />
          <h2>{game.name}</h2>
          <table className={mc.table}>
            <tbody>
              <tr>
                <td>
                  <img src="" alt="liste de jeu" />
                </td>
                <td>25/04/2023</td>
                <td>{gamePlayed.game_name}</td>
                <td>{gamePlayed.friend_id}</td>
              </tr>
            </tbody>
          </table>

          {/* Afficher les autres d√©tails du jeu ici */}
        </div>
      )}
    </div>
  );
};

export default Game;
