import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { createGame, updateGame } from "../../redux/reducers/game.slice";

const AddGame = () => {
  const dispatch = useDispatch();
  const { name } = useSelector((store) => store.games);

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(createGame(token, { name }));
  };

  const handleUpdateGame = (key, value) => {
    dispatch(updateGame({ key, value }));
  };
  return (
    <form on onSubmit={handleSubmit}>
      <input
        type="text"
        name="game"
        id="game"
        value={name}
        required
        onChange={(e) => handleUpdateGame("name", e.target.value)}
      />
      <input type="submit" />
    </form>
  );
};

export default AddGame;
