import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { signUp, updateUser } from "../../redux/reducers/user.slice";

const SignUp = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { name, firstname, email, number, password } = useSelector(
    (store) => store.user,
  );

  // const [name, setName] = useState(user.name);
  // const [firstname, setFirstName] = useState(user.firstname);
  // const [email, setEmail] = useState(user.email);
  // const [number, setNumber] = useState(user.number);
  // const [password, setPassword] = useState(user.password);

  const handleSignUp = () => {
    const redirect = () => {
      navigate("/");
    };
    dispatch(signUp(redirect));
  };

  const handleUpdateUser = (key, value) => {
    dispatch(updateUser({ key, value }));
  };

  return (
    <div>
      <h2>Sign up</h2>
      <form>
        <label>
          Name:
          <input
            type="text"
            value={name}
            onChange={(e) => handleUpdateUser("name", e.target.value)}
          />
        </label>
        <label>
          First name:
          <input type="text" />
        </label>
        <label>
          Email:
          <input type="email" />
        </label>
        <label>
          Number:
          <input type="text" />
        </label>
        <label>
          Password:
          <input type="password" />
        </label>
      </form>
      <button onClick={handleSignUp}>Sign up</button>
      <button onClick={handleUpdateUser}>Update user</button>
    </div>
  );
};

export default SignUp;
