import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import { getRequest, postRequest } from "../../api/api";
import { getItem } from "../../utils/storage.utils";

const token = getItem("token");

export const getFriends = createAsyncThunk("/friends", async (_, thunkApi) => {
  const { fulfillWithValue, rejectWithValue } = thunkApi;
  //   const token = getItem("token");
  const { status, result, error } = await getRequest(`/friends`, token);
  console.log(result);
  return error
    ? rejectWithValue(`Cannot get game - Error status ${status} - ${error}`)
    : fulfillWithValue(result);
});

const friendSlice = createSlice({
  name: "friends",
  initialState: {
    friends: [],
    name: "",
    loading: false,
    error: null,
  },
  reducers: {
    startLoading: (state) => {
      return { ...state, loading: true };
    },
    stopLoading: (state) => {
      return { ...state, loading: false };
    },
    updateFriends: (state, action) => {
      const { key, value } = action.payload;
      return { ...state, [key]: value };
      // state[key] = value;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(getFriends.fulfilled, (state, action) => {
        return {
          ...state,
          loading: false,
          friends: [...action.payload.games],
        };
      })
      .addCase(getFriends.rejected, (state, action) => {
        return { ...state, loading: false, error: action.payload };
      })
      .addCase(getFriends.pending, (state) => {
        return { ...state, loading: true };
      });
  },
});
