import React, { useState, useEffect, useCallback } from "react";
import { useDispatch, useSelector } from "react-redux";
import { NavLink, useNavigate } from "react-router-dom";
import {
  createFriend,
  getFriends,
  updateFriends,
} from "../../redux/reducers/friends.slice";
import { getItem } from "../../utils/storage.utils";

const Friend = React.memo(({ friend }) => {
  return (
    <NavLink to={`/friends/${friend.id}`}>
      <article>
        <h3>{friend.name}</h3>
        <p>{friend.email}</p>
      </article>
    </NavLink>
  );
});

const Friends = () => {
  const dispatch = useDispatch();
  const { friends } = useSelector((store) => store.friends);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const token = getItem("token");

  useEffect(() => {
    dispatch(getFriends(token));
  }, []);

  const handleCreateFriend = useCallback(
    (e) => {
      e.preventDefault();
      dispatch(createFriend({ name, email }, token)).then(() => {
        dispatch(getFriends(token));
        setName("");
        setEmail("");
      });
    },
    [name, email],
  );

  return (
    <div>
      {friends.map((friend) => (
        <Friend friend={friend} />
      ))}
      <form onSubmit={handleCreateFriend}>
        <input
          type="text"
          name="game"
          id="game"
          value={name}
          placeholder="Name"
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="email"
          name="email"
          id="email"
          value={email}
          placeholder="Email"
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input type="submit" />
      </form>
    </div>
  );
};

export default Friends;
