import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getOneFriend } from "../../redux/reducers/friends.slice";
import { getItem } from "../../utils/storage.utils";
import mc from "./friends.module.scss";

const Friend = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const token = getItem("token");

  const { loading, friends } = useSelector((store) => store.friends);

  useEffect(() => {
    dispatch(getOneFriend(id, token));
  }, [dispatch, id]);



  return (
    <div>
      {loading && <p>Loading...</p>}
      {friends && (
        <div className={mc.friendHeader}>
          <div className={mc.friendAvatar}></div>
          <div>
            <h2>{friends.name}</h2>
            <h4>{friends.email}</h4>
          </div>
        </div>
      )}
      {gamePlayed.map((gp) => {
        return (
          <table key={gp.id} className={mc.table}>
            <tbody>
              <tr>
                <td>
                  <img src="" alt="liste de jeu" />
                </td>
                <td>25/04/2023</td>
                <td>{gp.game_name}</td>
                <td>Joueur</td>
              </tr>
            </tbody>
          </table>
        );
      })}
    </div>
  );
};

export default Friend;
