import React from "react";
import { useState } from "react";
import { NavLink } from "react-router-dom";
import Buttons from "../buttons/Buttons";
import mc from "./nav.module.scss";

const Nav = () => {
  const [isActive, setIsActive] = useState(false);
  const [navHide, setNavHide] = useState(false);
  const [isVisible, setIsVisible] = useState(mc.overlay);
  const userConnected = () => {
    return true;
  };

  const handleClick = () => {
    setIsActive(!isActive);
    setNavHide(!navHide);
  };

  const handleClickOverlay = () => {
    setNavHide(!navHide);
  };

  return (
    <div>
      <button
        onClick={handleClick}
        className={`${mc.burger} ${isActive ? mc.burgerClicked : !isActive}`}
      >
        <span></span>
      </button>
      <nav className={`${!navHide ? mc.navHide : navHide}`}>
        <ul>
          <li>
            <ul>
              <li>
                <NavLink to="/">Accueil</NavLink>
              </li>
              <li>
                <NavLink to="/">Profil</NavLink>
              </li>
              <li>
                <NavLink to="/">Amis</NavLink>
              </li>
              <li>
                <NavLink to="/">Jeux</NavLink>
              </li>
            </ul>
          </li>
        </ul>
      </nav>
      <div onClick={handleClick} className={`${isVisible} ${isActive}`}></div>
    </div>
  );
};

export default Nav;
