import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import {
  getOneGame,
  modifyGame,
  startLoading,
  stopLoading,
  updateGame,
} from "../../redux/reducers/game.slice";
import mc from "./game.module.scss";

const GameCard = (props) => {
  const { game } = props;
  const navigate = useNavigate();
  const [showModal, setShowModal] = useState(false);
  const { games } = useSelector((store) => store.games);

  // const handleNavigateToGame = () => {
  //   navigate(`/game/${game.id}`);
  // };

  const handleEditGame = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(startLoading());
    dispatch(getOneGame(game.id)).then(() => {
      dispatch(stopLoading());
    });
  }, [dispatch, game.id]);

  const handleNameChange = (event) => {
    dispatch(updateGame({ key: "name", value: event.target.value }));
  };

  const handleSave = (e) => {
    e.preventDefault();
    dispatch(startLoading());
    dispatch(modifyGame({ gameId: game.id, name: game.name })).then(() => {
      dispatch(stopLoading());
    });
  };

  return (
    <div>
      <article className={mc.gameCard}>
        <img src={game.image} alt={game.name} />
        <h3>{game.name}</h3>
        <p>{game.scoreCount} games saved</p>
        <button className={mc.editButton} onClick={handleEditGame}>
          Edit
        </button>
      </article>
      {showModal && (
        <div className={mc.modal}>
          <div className={mc.modalContent}>
            <h2>Edit Game</h2>
            <p>Here you can edit the game information.</p>
            <form onSubmit={handleSave}>
              <label htmlFor="name">Name:</label>
              <input
                type="text"
                id="name"
                name="name"
                defaultValue={game.name}
                onChange={handleNameChange}
              />
              <label htmlFor="image">Image:</label>
              <input
                type="text"
                id="image"
                name="image"
                defaultValue={game.image}
              />
              <button className={mc.saveButton} type="submit">
                Save
              </button>
              <button
                className={mc.cancelButton}
                type="button"
                onClick={handleCloseModal}
              >
                Cancel
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default GameCard;
