import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getOneUser } from "../../redux/reducers/user.slice";
import { getItem } from "../../utils/storage.utils";
import mc from "./profile.module.scss";

const Profile = () => {
  const { user } = useSelector((store) => store.user);
  const dispatch = useDispatch();
  const token = getItem("token");
  // const userId = getItem("userId");

  useEffect(() => {
    dispatch(getOneUser(1, token));
  }, []);

  return (
    <div className={mc.profileContainer}>
      <div className={mc.profileHeader}>
        <img
          src={avatar}
          alt={`${user.name}'s avatar`}
          className={mc.profileAvatar}
        />
        <h1 className={mc.profileName}>{user.name}</h1>
        <p className={mc.profileEmail}>{user.email}</p>
      </div>
      <div className={mc.profileBio}>
        <p>{bio}</p>
      </div>
    </div>
  );
};

export default Profile;
