import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import { getRequest, postRequest } from "../../api/api";
import { getItem } from "../../utils/storage.utils";

export const createGame = createAsyncThunk(
  "/games/create",
  async (thunkApi) => {
    const { fulfillWithValue, rejectWithValue } = thunkApi;
    const token = getItem("token");
    const { status, result, error } = await postRequest(`/games/create`, token);
    return error
      ? rejectWithValue(
          `Cannot create game - Error status ${status} - ${error}`,
        )
      : fulfillWithValue(result);
  },
);

export const getGame = createAsyncThunk("/games", async (_, thunkApi) => {
  const { fulfillWithValue, rejectWithValue } = thunkApi;
  const token = getItem("token");
  const { status, result, error } = await getRequest(`/games`, token);
  console.log(result);
  return error
    ? rejectWithValue(`Cannot get game - Error status ${status} - ${error}`)
    : fulfillWithValue(result);
});

const gameSlice = createSlice({
  name: "games",
  initialState: {
    games: [],
    name: "",
    loading: false,
    error: null,
  },
  reducers: {
    startLoading: (state) => {
      return { ...state, loading: true };
    },
    stopLoading: (state) => {
      return { ...state, loading: false };
    },
    updateGame: (state, action) => {
      const { key, value } = action.payload;
      return { ...state, [key]: value };
      // state[key] = value;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(createGame.fulfilled, (state, action) => {
        console.log(action.payload);
        return { ...state, loading: false, games: [...action.payload] };
      })
      .addCase(createGame.rejected, (state, action) => {
        return { ...state, loading: false, error: action.payload };
      })
      .addCase(createGame.pending, (state) => {
        return { ...state, loading: true };
      })
      .addCase(getGame.fulfilled, (state, action) => {
        return {
          ...state,
          loading: false,
          games: { ...action.payload },
        };
      })
      .addCase(getGame.rejected, (state, action) => {
        return { ...state, loading: false, error: action.payload };
      })
      .addCase(getGame.pending, (state, action) => {
        return { ...state, loading: true };
      });
  },
});

export const { startLoading, stopLoading, updateGame } = gameSlice.actions;

export default gameSlice.reducer;
