import React from "react";
import { useState } from "react";
import { NavLink } from "react-router-dom";
import Buttons from "../buttons/Buttons";
import mc from "./nav.module.scss";

const Nav = () => {
  const [isActive, setIsActive] = useState(false);
  const [navHide, setNavHide] = useState(mc.navHide);
  const [isVisible, setIsVisible] = useState(mc.overlay);
  const userConnected = () => {
    return true;
  };

  const handleClick = () => {
    setIsActive(!isActive);
  };
  return (
    <div>
      <button
        onClick={handleClick}
        className={`${mc.burger} ${isActive ? mc.burgerClicked : !isActive}`}
      >
        <span></span>
      </button>
      <nav className={mc.navHide}>
        <ul>
          <li>
            <ul>
              <li>
                <NavLink to="/">Accueil</NavLink>
              </li>
              <li>
                <NavLink to="/">Profil</NavLink>
              </li>
              <li>
                <NavLink to="/">Amis</NavLink>
              </li>
              <li>
                <NavLink to="/">Jeux</NavLink>
              </li>
            </ul>
          </li>
        </ul>
      </nav>
      <div className={isVisible}></div>
    </div>
  );
};

export default Nav;
