import React from "react";
import { useState } from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { NavLink, useNavigate } from "react-router-dom";
import avatar from "../../assets/img/avatar.jpeg";
import mc from "./friends.module.scss";
import {
  createFriend,
  getFriends,
  updateFriends,
} from "../../redux/reducers/friends.slice";
import { getItem } from "../../utils/storage.utils";

const Friends = () => {
  const dispatch = useDispatch();
  const { friends } = useSelector((store) => store.friends);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const token = getItem("token");
  const [showModal, setShowModal] = useState(false);

  const handleEditGame = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  useEffect(() => {
    dispatch(getFriends(token));
  }, []);

  // const handleUpdateFriend = (key, value) => {
  //   dispatch(updateFriends({ key, value }));
  // };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(createFriend({ name, email }, token)).then(() => {
      dispatch(getFriends(token));
      setName("");
      setEmail("");
      setShowModal(false);
    });
  };
  return (
    <div>
      {Array.isArray(friends) &&
        friends.map((friend) => {
          return (
            <NavLink to={`/friends/${friend.id}`}>
              <article key={friend.id} className={mc.friendContainer}>
                <div className={mc.profileAvatar}>
                  <img
                    src={avatar}
                    alt="avatar de l'ami"
                    className={mc.profileAvatar}
                  />
                </div>

                <div className={mc.friendInfos}>
                  <h3>{friend.name}</h3>
                  <p>{friend.email}</p>
                </div>
              </article>
            </NavLink>
          );
        })}
      <button className={mc.addBtn} onClick={handleEditGame}>
        Ajouter un ami
      </button>
      {showModal && (
        <div className={mc.modal}>
          <div className={mc.modalContent}>
            <h2>Ajouter un ami</h2>
            <form onSubmit={handleSubmit}>
              <fieldset>
                <legend>Name</legend>
                <input
                  type="text"
                  name="name"
                  id="name"
                  value={name}
                  placeholder="Name"
                  onChange={(e) => setName(e.target.value)}
                  required
                />
              </fieldset>

              <fieldset>
                <legend>Email</legend>
                <input
                  type="email"
                  name="email"
                  id="email"
                  value={email}
                  placeholder="Email"
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </fieldset>

              <input type="submit" />
              <button
                className={mc.cancelButton}
                type="button"
                onClick={handleCloseModal}
              ></button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Friends;
