import { createSlice } from "@reduxjs/toolkit";
import { getRequest } from "../../api/api";

export const getGamePlayed = () => async (dispatch, getState) => {
  const loading = getState().gamesPlayed.loading;
  if (loading) return;
  dispatch(startLoading());
  const { status, result, error } = await getRequest(`/gamesplayed`);
  dispatch(setGamePlayed({ gamesPlayed: result }));
};

const gamePlayedSlice = createSlice({
  name: "gamesPlayed",
  initialState: {
    gamesPlayed: [],
    loading: false,
  },
  reducers: {
    startLoading: (state) => {
      return { ...state, loading: true };
    },
    stopLoading: (state) => {
      return { ...state, loading: false };
    },
    setGamePlayed: (state, action) => {
      return {
        ...state,
        loading: false,
        gamesPlayed: [...action.payload.gamesPlayed],
      };
    },
  },
});

export const { startLoading, stopLoading, setGamePlayed } =
  gamePlayedSlice.actions;

export default gamePlayedSlice.reducer;
