import React from "react";
import GameCard from "./GameCard";
import mc from "./game.module.scss";
import { useDispatch } from "react-redux";
import { getGame } from "../../redux/reducers/game.slice";
import { useEffect } from "react";
import { getItem } from "../../utils/storage.utils";

const Game = () => {
  const dispatch = useDispatch();
  const token = getItem("token");

  useEffect(() => {
    dispatch(getGame());
  });

  const games = [
    {
      id: 1,
      name: "Monopoly",
      image: "https://www.example.com/monopoly.png",
      scoreCount: 3,
    },
    {
      id: 2,
      name: "Scrabble",
      image: "https://www.example.com/scrabble.png",
      scoreCount: 5,
    },
    {
      id: 3,
      name: "Chess",
      image: "https://www.example.com/chess.png",
      scoreCount: 2,
    },
  ];

  return (
    <div className={mc.gameListContainer}>
      <h2>My Games</h2>
      <div className={mc.gameList}>
        {games.map((game) => (
          <GameCard key={game.id} game={game} />
        ))}
      </div>
    </div>
  );
};

export default Game;
