import { useParams } from "react-router-dom";

const Game = () => {
  const { id } = useParams(); // récupérer l'ID du jeu depuis les paramètres d'URL
  const { games } = useSelector((store) => store.games);

  const game = games.find((g) => g.id === id); // trouver le jeu correspondant dans la liste des jeux

  return (
    <div>
      <h2>{game.name}</h2>
      <p>{game.scoreCount} games saved</p>
    </div>
  );
};

export default Game;
