import React, { useState } from "react";
import GameCard from "./GameCard";
import mc from "./game.module.scss";
import { useDispatch, useSelector } from "react-redux";
import { createGame, getGame } from "../../redux/reducers/game.slice";
import { useEffect } from "react";
import { getItem } from "../../utils/storage.utils";
import AddGame from "./AddGame";

const Game = () => {
  const dispatch = useDispatch();
  const { games, loading, error } = useSelector((store) => store.games);
  const token = getItem("token");
  const [name, setName] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(createGame(name, token));
    setName("");
  };

  useEffect(() => {
    dispatch(getGame(token));
  }, [dispatch, token]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  console.log(games.length > 0 ? games : "No games found");

  return (
    <div className={mc.gameListContainer}>
      <h2>My Games</h2>
      <div className={mc.gameList}>
        {games.map((game) => (
          <GameCard key={game.id} game={game} />
        ))}
      </div>
      {/* <AddGame /> */}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="game"
          id="game"
          value={name}
          required
          onChange={(e) => setName(e.target.value)}
        />
        <input type="submit" />
      </form>
    </div>
  );
};

export default Game;
