import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { signUp, updateUser } from "../../redux/reducers/user.slice";

const SignUp = () => {
  const dispatch = useDispatch();

  const { name, firstname, email, number, password, loading } = useSelector(
    (store) => store.user,
  );
  // const [name, setName] = useState(user.name);
  // const [firstname, setFirstName] = useState(user.firstname);
  // const [email, setEmail] = useState(user.email);
  // const [number, setNumber] = useState(user.number);
  // const [password, setPassword] = useState(user.password);

  const handleSignUp = (e) => {
    e.preventDefault();
    dispatch(signUp(updateUser({ name, firstname, email, number, password })));
  };

  const handleUpdateUser = () => {
    dispatch(updateUser({ name, firstname, email, number, password }));
  };

  return (
    <div>
      <h2>Sign up</h2>
      <form>
        <label>
          Name:
          <input type="text" />
        </label>
        <label>
          First name:
          <input type="text" />
        </label>
        <label>
          Email:
          <input type="email" />
        </label>
        <label>
          Number:
          <input type="text" />
        </label>
        <label>
          Password:
          <input type="password" />
        </label>
        <button type="submit" onClick={handleSignUp}>
          Sign up
        </button>
      </form>
    </div>
  );
};

export default SignUp;
