import { createSlice } from "@reduxjs/toolkit";

export const getGamePlayed = (redirect, form) => async (dispatch, getState) => {
  const loading = getState().gamePlayed.loading;
  if (loading) return;
  dispatch(startLoading());
  const { status, result, error } = await getRequest(`/gamesplayed`, form);
  console.log(status, error, result);
  if (status >= 200 && status < 300) {
    setItem("token", result.token);
    redirect();
  }
  dispatch(stopLoading());
};

const gamePlayedSlice = createSlice({
  name: "gamePlayed",
  initialState: {
    gamesPlayed: [],
    loading: false,
  },
  reducers: {
    startLoading: (state) => {
      return { ...state, loading: true };
    },
    stopLoading: (state) => {
      return { ...state, loading: false };
    },
    setGamePlayed: (state, action) => {
      return {
        ...state,
        loading: false,
        gamesPlayed: [...action.payload.gamesPlayed],
      };
    },
  },
});

export const { startLoading, stopLoading, setGamePlayed } =
  gamePlayedSlice.actions;

export default gamePlayedSlice.reducer;
