import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getOneGame } from "../../redux/reducers/game.slice";
import { getGamePlayed } from "../../redux/reducers/gamePlayed.slice";
import { getItem } from "../../utils/storage.utils";
import mc from "./game.module.scss";

const Game = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const token = getItem("token");
  const { loading, game } = useSelector((store) => store.games);

  const { gamesPlayed } = useSelector((store) => store.gamesPlayed);
  const gamePlayed = gamesPlayed.filter((gp) => gp.game_id === game.id);
  console.log(gamePlayed);

  useEffect(() => {
    dispatch(getGamePlayed(token));
  }, []);

  useEffect(() => {
    dispatch(getOneGame(id, token));
  }, [dispatch, id]);

  return (
    <div>
      {loading && <p>Loading...</p>}
      {game && (
        <div className={mc.gameContainer}>
          <img src="" alt="une photo du jeu" />
          <h2 className={mc.title}>{game.name}</h2>
          {gamePlayed.map((gp) => {
            return (
              <table key={gp.id} className={mc.table}>
                <tbody>
                  <tr>
                    <td>
                      <img src="" alt="liste de jeu" />
                    </td>
                    <td>25/04/2023</td>
                    <td>{gp.game_name}</td>
                    <td>Joueur</td>
                  </tr>
                </tbody>
              </table>
            );
          })}
          <div>
            <article className={mc.gameCard} onClick={handleNavigateToGame}>
              <img src={game.image} alt={game.name} />
              <h3>{game.name}</h3>
              <p>{game.scoreCount} games saved</p>
              <button className={mc.editButton} onClick={handleEditGame}>
                Edit
              </button>
            </article>
            {showModal && (
              <div className={mc.modal}>
                <div className={mc.modalContent}>
                  <h2>Edit Game</h2>
                  <p>Here you can edit the game information.</p>
                  <form>
                    <label htmlFor="name">Name:</label>
                    <input
                      type="text"
                      id="name"
                      name="name"
                      defaultValue={game.name}
                    />
                    <label htmlFor="image">Image:</label>
                    <input
                      type="text"
                      id="image"
                      name="image"
                      defaultValue={game.image}
                    />
                    <button className={mc.saveButton} type="submit">
                      Save
                    </button>
                    <button
                      className={mc.cancelButton}
                      type="button"
                      onClick={handleCloseModal}
                    >
                      Cancel
                    </button>
                  </form>
                </div>
              </div>
            )}
          </div>

          {/* Afficher les autres d√©tails du jeu ici */}
        </div>
      )}
    </div>
  );
};

export default Game;
