import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getOneGame } from "../redux/slices/games.slice";

const Game = ({ match }) => {
  const { gameId } = match.params;

  const dispatch = useDispatch();

  const { loading, games } = useSelector((state) => state.games);

  useEffect(() => {
    dispatch(getOneGame(gameId));
  }, [dispatch, gameId]);

  const game = games.find((g) => g._id === gameId);

  return (
    <div>
      {loading && <p>Loading...</p>}
      {game && (
        <div>
          <h1>{game.name}</h1>

          {/* Afficher les autres d√©tails du jeu ici */}
        </div>
      )}
    </div>
  );
};

export default Game;
