import { postRequest } from "../../api/api";
import { createSlice } from "@reduxjs/toolkit";
import { getItem, setItem } from "../../utils/storage.utils";

export const signUp = (redirect, form) => async (dispatch, getState) => {
  const loading = getState().user.loading;
  if (loading) return;
  dispatch(startLoading());
  const { status, result, error } = await postRequest(`/users/sign-up`, form);
  console.log(status, error, result);
  if (status >= 200 && status < 300) {
    setItem("token", result.token);
    redirect();
  }
  dispatch(stopLoading());
};

export const signIn = (redirect, form) => async (dispatch, getState) => {
  const loading = getState().user.loading;
  if (loading) return;
  dispatch(startLoading());
  const { status, result, error } = await postRequest(`/users/sign-in`, form);
  console.log(status, error, result);
  if (status >= 200 && status < 300) {
    setItem("token", result.token);
    redirect();
  }
  dispatch(stopLoading());
};

export const userSlice = createSlice({
  name: "user",
  initialState: {
    name: "",
    firstname: "",
    email: "",
    number: 0,
    password: "",
    loading: false,
    isConnected: true,
  },
  reducers: {
    startLoading: (state) => {
      return { ...state, loading: true };
    },
    stopLoading: (state) => {
      return { ...state, loading: false };
    },
    updateUser: (state, action) => {
      const { key, value } = action.payload;
      return { ...state, [key]: value };
      // state[key] = value;
    },
    userConnected: (state, action) => {
      const token = getItem("token");
      return { ...state, isConnected: action };
    },
  },
});

export const { startLoading, stopLoading, updateUser, userConnected } =
  userSlice.actions;
export default userSlice.reducer;
