import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getOneFriend } from "../../redux/reducers/friends.slice";
import { getGamePlayed } from "../../redux/reducers/gamePlayed.slice";
import { getItem } from "../../utils/storage.utils";
import mc from "./friends.module.scss";

const Friend = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const token = getItem("token");

  const { loading, friends } = useSelector((store) => store.friends);

  const { gamesPlayed } = useSelector((store) => store.gamesPlayed);
  const gamePlayed = gamesPlayed.filter((gp) => gp.friend_id === friends.id);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    dispatch(getGamePlayed(token));
  }, []);

  useEffect(() => {
    dispatch(getOneFriend(id, token));
  }, [dispatch, id]);

  const handleEditGame = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleNameChange = (event) => {
    dispatch(updateGame({ key: "name", value: event.target.value }));
  };
  const handleSave = (e) => {
    e.preventDefault();
    dispatch(startLoading());
