      dispatch(getOneGame(id, token));
    });
    setShowModal(false);
  };

  return (
    <div>
      {loading && <p>Loading...</p>}
      {friends && (
        <div className={mc.friendHeader}>
          <div className={mc.friendAvatar}>Image Amis</div>
          <div>
            <h2>{friends.name}</h2>
            <h4>{friends.email}</h4>
          </div>
        </div>
      )}
      {gamePlayed.map((gp) => {
        return (
          <table key={gp.id} className={mc.table}>
            <tbody>
              <tr>
                <td>
                  <img src="" alt="liste de jeu" />
                </td>
                <td>25/04/2023</td>
                <td>{gp.game_name}</td>
                <td>{friends.name}</td>
              </tr>
            </tbody>
          </table>
        );
      })}
    </div>
  );
};

export default Friend;
