import React from "react";
import { useState } from "react";
import { NavLink } from "react-router-dom";
import Buttons from "../buttons/Buttons";
import mc from "./nav.module.scss";
import { useDispatch, useSelector } from "react-redux";
import { getToken } from "../../redux/reducers/auth.slice";
import { getItem } from "../../utils/storage.utils";
import { useEffect } from "react";

const Nav = () => {
  const [isActive, setIsActive] = useState(false);
  const [navHide, setNavHide] = useState(false);
  const { isAuthenticated } = useSelector((store) => store.auth);

  const dispatch = useDispatch();

  const handleClick = () => {
    setIsActive(!isActive);
    setNavHide(!navHide);
  };

  const token = getItem("token");

  const userConnected = () => {
    dispatch(getToken(token));
    console.log();
  };

  return (
    <div>
      {() =>
        userConnected() ? (
          <div>
            <button
              onClick={handleClick}
              className={`${mc.burger} ${
                isActive ? mc.burgerClicked : !isActive
              }`}
            >
              <span></span>
            </button>
            <nav
              className={`${!navHide ? mc.navHide : navHide} ${
                mc.navConnected
              }`}
            >
              <ul>
                <li>
                  <ul>
                    <li>
                      <NavLink to="/">Accueil</NavLink>
                    </li>
                    <li>
                      <NavLink to="/">Profil</NavLink>
                    </li>
                    <li>
                      <NavLink to="/">Amis</NavLink>
                    </li>
                    <li>
                      <NavLink to="/">Jeux</NavLink>
                    </li>
                  </ul>
                </li>
              </ul>
            </nav>
            <div
              onClick={handleClick}
              className={`${mc.overlay} ${isActive ? mc.show : !isActive}`}
            ></div>
          </div>
        ) : (
          <Buttons />
        )
      }
    </div>
  );
};

export default Nav;
