import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getFriends } from "../../redux/reducers/friends.slice";
import { getItem } from "../../utils/storage.utils";

const Friends = () => {
  const dispatch = useDispatch();
  const { friends, name } = useSelector((store) => store.friends);
  const token = getItem("token");

  useEffect(() => {
    dispatch(getFriends(token));
  }, []);
  console.log(friends);
  return (
    <div>
      {friends.map((friend) => {
        return (
          <>
            <article key={friend.id}>
              <h3>{friend.name}</h3>
              <h3>{friend.email}</h3>
            </article>
            <form>
              <input type="text" name="game" id="game" value={name} required />
              <input type="submit" />
            </form>
          </>
        );
      })}
    </div>
  );
};

export default Friends;
