import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getFriends, updateFriends } from "../../redux/reducers/friends.slice";
import { getItem } from "../../utils/storage.utils";

const Friends = () => {
  const dispatch = useDispatch();
  const { friends, name, email } = useSelector((store) => store.friends);
  const token = getItem("token");

  useEffect(() => {
    dispatch(getFriends(token));
  }, []);

  const handleUpdateFriend = (key, value) => {
    dispatch(updateFriends({ key, value }));
  };
  console.log(friends);
  return (
    <div>
      {friends.map((friend) => {
        return (
          <>
            <article key={friend.id}>
              <h3>{friend.name}</h3>
              <h3>{friend.email}</h3>
            </article>
            <form>
              <input
                type="text"
                name="game"
                id="game"
                value={name}
                placeholder="Name"
                onChange={(e) => handleUpdateFriend("name", e.target.value)}
                required
              />
              <input
                type="email"
                name="email"
                id="email"
                value={email}
                placeholder="Email"
                onChange={(e) => handleUpdateFriend("email", e.target.value)}
                required
              />
              <input type="submit" />
            </form>
          </>
        );
      })}
    </div>
  );
};

export default Friends;
